from unittest import TestCase

from pydantic import ValidationError

from bb_wrapper.models.barcode import BarcodeCobranca


class BarcodeCobrancaTestCase(TestCase):
    maxDiff = None

    barcode = "00196846200000100000000003128557123123000917"
    code_line = "00190000090312855712531230009172684620000010000"
    barcode_image = "data:image/png;base64,"  # noqa

    def test_validate_barcode_valid(self):
        BarcodeCobranca(barcode=self.barcode)

    def test_validate_barcode_invalid(self):
        with self.assertRaises(ValidationError):
            BarcodeCobranca(barcode="".zfill(44))

    def test_validate_code_line_valid(self):
        BarcodeCobranca(code_line=self.code_line)

    def test_validate_code_line_invalid(self):
        with self.assertRaises(ValidationError):
            BarcodeCobranca(code_line="".zfill(47))

    def test_invalid(self):
        with self.assertRaises(ValidationError):
            BarcodeCobranca()

    def test_set_code_line(self):
        instance = BarcodeCobranca(barcode=self.barcode)

        self.assertEqual(instance.code_line, self.code_line)

    def test_set_barcode(self):
        instance = BarcodeCobranca(code_line=self.code_line)

        self.assertEqual(instance.barcode, self.barcode)

    def test_set_barcode_image(self):
        instance = BarcodeCobranca(barcode=self.barcode)

        result = instance.barcode_image

        self.assertEqual(instance.barcode_image, self.barcode_image, msg=result)
